int i, menuOption, textSize, mainLoop = TRUE;
char string[100];

int validateInput(){
    for (i = 0; i < textSize; i++){
        if (string[i]<65 || string[i]>90){
            printf("The text format is invalid.\nExample: HELLOWORLD\n");
            return FALSE;
        }   
    }
    return TRUE;
}

