void compile(){
    system("gcc chao_opt.c");
    system("gcc chao_opt.c -o chao_opt");
    printf("Compilation complete\n");
}

void showMenu(){
    printf("\nProgram Menu\n"
        "1 - exit\n2 - run code\n3 - compile code\n4 - run worst cenary\n"
        "Choose a option: ");
    scanf("%d", &menuOption);
}

int main(int argc, char const *argv[]){

    do{

        showMenu();
        switch (menuOption){
        case 1:
            mainLoop = FALSE;
            break;
        case 2:
            execute();
            break;
        case 3:
            compile();
            break;
        case 4:
            runWorstCenary();
            break;
        default:
            printf("This option is invalid");
            break;
        }
    } while (mainLoop);
    
    return 0;
}
